// frontend/src/pages/ReportForm.jsx
// Page for submitting a new civic issue report.

import React, { useState } from 'react';
import { submitComplaint } from '../services/api';
import Spinner from '../components/Spinner';

function ReportForm() {
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState({ lat: '', lon: '' });
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleGetLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            lat: position.coords.latitude,
            lon: position.coords.longitude,
          });
        },
        () => {
          setError('Unable to retrieve location. Please enter it manually.');
        }
      );
    } else {
      setError('Geolocation is not supported by this browser.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!description || !location.lat || !location.lon || !image) {
      setError('All fields, including an image, are required.');
      return;
    }
    setLoading(true);
    setError('');
    setSuccess('');

    const formData = new FormData();
    formData.append('description', description);
    formData.append('latitude', location.lat);
    formData.append('longitude', location.lon);
    formData.append('image', image);
    formData.append('userId', 1); // Hardcoded for now, replace with auth user ID

    try {
      const response = await submitComplaint(formData);
      setSuccess(`Complaint submitted successfully! Your Complaint ID is: ${response.data.complaintId}`);
      // Reset form
      setDescription('');
      setLocation({ lat: '', lon: '' });
      setImage(null);
      e.target.reset();
    } catch (err) {
      setError('Failed to submit complaint. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-6 text-gray-800">Report a Civic Issue</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 font-semibold mb-2">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="4"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 font-semibold mb-2">Location</label>
          <div className="flex items-center space-x-4">
            <input
              type="text"
              placeholder="Latitude"
              value={location.lat}
              onChange={(e) => setLocation({ ...location, lat: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
              required
            />
            <input
              type="text"
              placeholder="Longitude"
              value={location.lon}
              onChange={(e) => setLocation({ ...location, lon: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
              required
            />
            <button type="button" onClick={handleGetLocation} className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 whitespace-nowrap">
              Get Location
            </button>
          </div>
        </div>

        <div className="mb-6">
           <label htmlFor="image" className="block text-gray-700 font-semibold mb-2">Upload Photo</label>
           <input
             type="file"
             id="image"
             accept="image/*"
             onChange={(e) => setImage(e.target.files[0])}
             className="w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
             required
           />
        </div>

        {error && <p className="text-red-500 mb-4">{error}</p>}
        {success && <p className="text-green-500 mb-4">{success}</p>}

        <button type="submit" disabled={loading} className="w-full bg-blue-600 text-white font-bold py-3 px-4 rounded-md hover:bg-blue-700 disabled:bg-blue-300 flex items-center justify-center">
          {loading ? <Spinner /> : 'Submit Report'}
        </button>
      </form>
    </div>
  );
}

export default ReportForm;
