// frontend/src/components/Header.jsx
import React, { useContext } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

function Header() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };
  
  const activeLinkStyle = {
    color: '#2563eb', // A blue color from Tailwind's default palette
    textDecoration: 'underline',
  };

  return (
    <header className="bg-white shadow-md">
      <nav className="container mx-auto px-4 py-4 flex justify-between items-center">
        <NavLink to="/" className="text-xl font-bold text-gray-800">
          Jan Suvida
        </NavLink>
        <div className="flex items-center space-x-6">
  <NavLink to="/feed" style={({ isActive }) => (isActive ? activeLinkStyle : undefined)} className="text-gray-600 hover:text-blue-600">
    Feed
  </NavLink>
          {user && ( // Only show if logged in
            <NavLink to="/report" style={({ isActive }) => (isActive ? activeLinkStyle : undefined)} className="text-gray-600 hover:text-blue-600">
              Report Issue
            </NavLink>
          )}
          <NavLink to="/track" style={({ isActive }) => (isActive ? activeLinkStyle : undefined)} className="text-gray-600 hover:text-blue-600">
            Track Complaint
          </NavLink>

          {user ? (
            <button onClick={handleLogout} className="bg-red-500 text-white px-3 py-1 rounded-md text-sm hover:bg-red-600">
              Logout
            </button>
          ) : (
            <>
              <NavLink to="/login" style={({ isActive }) => (isActive ? activeLinkStyle : undefined)} className="text-gray-600 hover:text-blue-600">
                Login
              </NavLink>
              <NavLink to="/register" style={({ isActive }) => (isActive ? activeLinkStyle : undefined)} className="bg-blue-600 text-white px-3 py-1 rounded-md text-sm hover:bg-blue-700">
                Sign Up
              </NavLink>
            </>
          )}
        </div>
      </nav>
    </header>
  );
}

export default Header;
