// frontend/src/pages/ReportForm.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { submitComplaint } from '../services/api';
import Spinner from '../components/Spinner';

// Icons
const LocationIcon = <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor"><path fillRule="evenodd" d="M5.05 4.05a7 7 0 119.9 9.9L10 18.9l-4.95-4.95a7 7 0 010-9.9zM10 11a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" /></svg>;
const UploadIcon = <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path strokeLinecap="round" strokeLinejoin="round" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" /></svg>;


function ReportForm() {
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState({ lat: '', lon: '' });
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null); // For showing the selected image
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  // Handle image selection and create a preview
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      // Create a URL for the file to use in the preview
      setImagePreview(URL.createObjectURL(file));
    }
  };

  // Clean up the created object URL to prevent memory leaks
  useEffect(() => {
    return () => {
      if (imagePreview) {
        URL.revokeObjectURL(imagePreview);
      }
    };
  }, [imagePreview]);
  
  const handleGetLocation = () => {
    setLoading(true);
    setError('');
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setLocation({
          lat: position.coords.latitude.toFixed(6),
          lon: position.coords.longitude.toFixed(6),
        });
        setLoading(false);
      },
      () => {
        setError('Unable to retrieve location. Please enter it manually.');
        setLoading(false);
      }
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!description || !location.lat || !location.lon || !image) {
      setError('Please fill out all fields and upload an image.');
      return;
    }
    setLoading(true);
    setError('');
    setSuccess('');

    const formData = new FormData();
    // We get the user ID from the logged in user context
    const userId = JSON.parse(atob(localStorage.getItem('token').split('.')[1])).id;
    formData.append('description', description);
    formData.append('latitude', location.lat);
    formData.append('longitude', location.lon);
    formData.append('image', image);
    formData.append('userId', userId);

    try {
      const response = await submitComplaint(formData);
      setSuccess(`Success! Complaint ID: ${response.data.complaintId}. You will be redirected shortly.`);
      setTimeout(() => navigate('/'), 3000); // Redirect to home after 3 seconds
    } catch (err) {
      setError('Failed to submit complaint. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 h-full flex flex-col">
      <div className="text-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">Report a New Issue</h1>
        <p className="text-sm text-gray-500">Let's make our city better, together.</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6 flex-grow flex flex-col">
        {/* Description */}
        <div>
          <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
            Describe the Issue
          </label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="3"
            placeholder="e.g., Large pothole in the middle of the road..."
            required
          />
        </div>

        {/* Location */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
          <div className="bg-gray-50 p-3 rounded-lg border">
            <button type="button" onClick={handleGetLocation} disabled={loading} className="w-full flex items-center justify-center px-4 py-2 bg-white text-gray-700 rounded-md border shadow-sm hover:bg-gray-100 disabled:opacity-50">
              {LocationIcon} Get My Location
            </button>
            <div className="flex items-center space-x-2 mt-3">
              <input type="text" placeholder="Latitude" value={location.lat} onChange={(e) => setLocation({ ...location, lat: e.target.value })} className="w-full px-3 py-2 border border-gray-300 rounded-md" required />
              <input type="text" placeholder="Longitude" value={location.lon} onChange={(e) => setLocation({ ...location, lon: e.target.value })} className="w-full px-3 py-2 border border-gray-300 rounded-md" required />
            </div>
          </div>
        </div>

        {/* Image Upload */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Photo of the Issue</label>
          {imagePreview ? (
            <div className="text-center">
              <img src={imagePreview} alt="Issue preview" className="w-full h-40 object-cover rounded-lg mb-2" />
              <button type="button" onClick={() => { setImage(null); setImagePreview(null); }} className="text-sm text-red-600 hover:underline">Remove Image</button>
            </div>
          ) : (
            <label htmlFor="image-upload" className="flex flex-col items-center justify-center w-full h-32 px-4 py-6 bg-white border-2 border-gray-300 border-dashed rounded-lg cursor-pointer hover:bg-gray-50">
              {UploadIcon}
              <span className="mt-2 text-sm text-gray-600">Click to upload a photo</span>
              <span className="text-xs text-gray-500">PNG, JPG, GIF up to 5MB</span>
            </label>
          )}
          <input id="image-upload" type="file" className="hidden" accept="image/*" onChange={handleImageChange} />
        </div>

        {/* Spacer to push button to the bottom */}
        <div className="flex-grow"></div>

        {/* Submit Button & Feedback */}
        <div className="flex-shrink-0">
          {error && <p className="text-red-500 text-center text-sm mb-2">{error}</p>}
          {success && <p className="text-green-600 text-center text-sm mb-2">{success}</p>}
          <button type="submit" disabled={loading} className="w-full bg-blue-600 text-white font-bold py-3 px-4 rounded-lg hover:bg-blue-700 disabled:bg-blue-300 flex items-center justify-center">
            {loading ? <Spinner /> : 'Submit Report'}
          </button>
        </div>
      </form>
    </div>
  );
}

export default ReportForm;
