import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { submitComplaint } from '../services/api';
import Spinner from '../components/Spinner';

// Icons
const MicIcon = <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor"><path d="M7 4a3 3 0 016 0v6a3 3 0 11-6 0V4z" /><path fillRule="evenodd" d="M5.5 8.5A.5.5 0 016 9v1a4 4 0 004 4 .5.5 0 010 1 5 5 0 01-5-5V9a.5.5 0 01.5-.5z" clipRule="evenodd" /></svg>;
const LocationIcon = <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor"><path fillRule="evenodd" d="M5.05 4.05a7 7 0 119.9 9.9L10 18.9l-4.95-4.95a7 7 0 010-9.9zM10 11a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" /></svg>;
const UploadIcon = <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path strokeLinecap="round" strokeLinejoin="round" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" /></svg>;

// Speech Recognition setup
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const mic = SpeechRecognition ? new SpeechRecognition() : null;
if (mic) {
  mic.continuous = true;
  mic.interimResults = true;
  mic.lang = 'en-US';
}

function ReportForm() {
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState(''); // New state for category
  const [isListening, setIsListening] = useState(false); // New state for mic
  // ... (other state variables remain the same)
  const [location, setLocation] = useState({ lat: '', lon: '' });
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (!mic) return;
    // Handle speech recognition results
    mic.onresult = (event) => {
      const transcript = Array.from(event.results)
        .map((result) => result[0])
        .map((result) => result.transcript)
        .join('');
      setDescription(transcript);
    };
  }, []);

  const handleListen = () => {
    if (!mic) return;
    if (isListening) {
      mic.stop();
      setIsListening(false);
    } else {
      mic.start();
      setIsListening(true);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!description || !location.lat || !location.lon || !image || !category) {
      setError('Please fill out all fields and upload an image.');
      return;
    }
    setLoading(true);
    // ... (rest of handleSubmit is the same, just add category to FormData)
    const formData = new FormData();
    const userId = JSON.parse(atob(localStorage.getItem('token').split('.')[1])).id;
    formData.append('description', description);
    formData.append('latitude', location.lat);
    formData.append('longitude', location.lon);
    formData.append('image', image);
    formData.append('userId', userId);
    formData.append('category', category); // Add category to form data

    try {
      const response = await submitComplaint(formData);
      setSuccess(`Success! Complaint ID: ${response.data.complaintId}. Redirecting...`);
      setTimeout(() => navigate('/'), 3000);
    } catch (err) {
      setError('Failed to submit complaint. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // ... (handleImageChange, handleGetLocation, and useEffect for imagePreview are unchanged)

  const issueCategories = Array.from({ length: 10 }, (_, i) => `Issue ${i + 1}`);

  return (
    <div className="p-4 h-full flex flex-col">
      <div className="text-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">Report a New Issue</h1>
        <p className="text-sm text-gray-500">Your report makes a difference.</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6 flex-grow flex flex-col">
        {/* Category Dropdown */}
        <div>
          <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">
            Category of Issue
          </label>
          <select
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="" disabled>Select a category</option>
            {issueCategories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
          </select>
        </div>

        {/* Description */}
        <div>
          <div className="flex justify-between items-center mb-1">
            <label htmlFor="description" className="block text-sm font-medium text-gray-700">
              Describe the Issue
            </label>
            {mic && (
              <button type="button" onClick={handleListen} className={`p-1.5 rounded-full ${isListening ? 'bg-red-500 text-white' : 'bg-gray-200 text-gray-600'}`}>
                {MicIcon}
              </button>
            )}
          </div>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="3"
            placeholder={isListening ? "Listening..." : "e.g., Large pothole..."}
            required
          />
        </div>

        {/* ... (Location and Image Upload sections remain the same) ... */}
        
        {/* Spacer to push button to the bottom */}
        <div className="flex-grow"></div>

        {/* Submit Button & Feedback */}
        <div className="flex-shrink-0">
          {error && <p className="text-red-500 text-center text-sm mb-2">{error}</p>}
          {success && <p className="text-green-600 text-center text-sm mb-2">{success}</p>}
          <button type="submit" disabled={loading} className="w-full bg-blue-600 text-white font-bold py-3 px-4 rounded-lg hover:bg-blue-700 disabled:bg-blue-300 flex items-center justify-center">
            {loading ? <Spinner /> : 'Submit Report'}
          </button>
        </div>
      </form>
    </div>
  );
}

export default ReportForm;
